import React, { useState, useEffect } from "react";
import {
  connectWallet,
  startLottery,
  endLottery,
  buyTicket,
  getAllLotteries,
} from "./contract";

function App() {
  const [account, setAccount] = useState("");
  const [ticketPrice, setTicketPrice] = useState("");
  const [lotteryId, setLotteryId] = useState("");
  const [amount, setAmount] = useState("");
  const [lotteries, setLotteries] = useState([]);

  useEffect(() => {
    loadLotteries();
  }, []);

  const loadLotteries = async () => {
    try {
      const data = await getAllLotteries();
      setLotteries(data);
    } catch (err) {
      console.error(err);
    }
  };

  const connect = async () => {
    const acc = await connectWallet();
    if (acc) setAccount(acc);
  };

  const handleStartLottery = async () => {
    await startLottery(ticketPrice);
    loadLotteries();
  };

  const handleEndLottery = async () => {
    await endLottery(lotteryId);
    loadLotteries();
  };

  const handleBuyTicket = async () => {
    await buyTicket(lotteryId, amount);
    loadLotteries();
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Lottery DApp</h1>
      <p>Connected Account: {account}</p>
      <button onClick={connect}>Connect Wallet</button>

      <h3>Start Lottery</h3>
      <input
        placeholder="Ticket Price in ETH"
        value={ticketPrice}
        onChange={(e) => setTicketPrice(e.target.value)}
      />
      <button onClick={handleStartLottery}>Start</button>

      <h3>End Lottery</h3>
      <input
        placeholder="Lottery ID"
        value={lotteryId}
        onChange={(e) => setLotteryId(e.target.value)}
      />
      <button onClick={handleEndLottery}>End</button>

      <h3>Buy Ticket</h3>
      <input
        placeholder="Lottery ID"
        value={lotteryId}
        onChange={(e) => setLotteryId(e.target.value)}
      />
      <input
        placeholder="Amount in ETH"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
      />
      <button onClick={handleBuyTicket}>Buy</button>

      <h3>All Lotteries</h3>
      {lotteries.map((lottery, index) => (
        <div key={index}>
          <p>Lottery ID: {index}</p>
          <p>Ticket Price: {ethers.formatEther(lottery.ticketPrice)} ETH</p>
          <p>Active: {lottery.isActive.toString()}</p>
          <hr />
        </div>
      ))}
    </div>
  );
}

export default App;
