import React, { useState } from 'react';
import { Button, TextField } from '@mui/material';
import { getContract } from '../DecentralizedIdentity';

const GetCredential = () => {
  const [holder, setHolder] = useState('');
  const [credential, setCredential] = useState('');

  const handleGet = async () => {
    const contract = await getContract();
    if (contract) {
      const result = await contract.getCredential(holder);
      setCredential(result);
    }
  };

  return (
    <div>
      <TextField label="Holder Address" value={holder} onChange={(e) => setHolder(e.target.value)} fullWidth margin="normal" />
      <Button variant="contained" onClick={handleGet}>Get Credential</Button>
      {credential && <p>Credential: {credential}</p>}
    </div>
  );
};

export default GetCredential;
