import React, { useState } from 'react';
import { Container, Typography, Button } from '@mui/material';
import { connectWallet } from './web3';
import CreateDID from './CreateDID';
import IssueCredential from './IssueCredential';
import GrantAccess from './GrantAccess';
import GetCredential from './GetCredential';

function App() {
  const [account, setAccount] = useState('');

  const handleConnectWallet = async () => {
    const acc = await connectWallet();
    setAccount(acc);
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom mt={3}>Decentralized Identity App</Typography>
      {!account ? (
        <Button variant="contained" onClick={handleConnectWallet}>Connect Wallet</Button>
      ) : (
        <>
          <Typography variant="subtitle1" gutterBottom>Connected Account: {account}</Typography>
          <CreateDID />
          <IssueCredential />
          <GrantAccess />
          <GetCredential />
        </>
      )}
    </Container>
  );
}

export default App;
