import React, { useState } from 'react';
import { Button, TextField } from '@mui/material';
import { getContract } from '../DecentralizedIdentity';

const IssueCredential = () => {
  const [receiver, setReceiver] = useState('');
  const [credential, setCredential] = useState('');

  const handleIssue = async () => {
    const contract = await getContract();
    if (contract) {
      const tx = await contract.issueCredential(receiver, credential);
      await tx.wait();
      alert('Credential issued successfully!');
    }
  };

  return (
    <div>
      <TextField label="Receiver Address" value={receiver} onChange={(e) => setReceiver(e.target.value)} fullWidth margin="normal" />
      <TextField label="Credential" value={credential} onChange={(e) => setCredential(e.target.value)} fullWidth margin="normal" />
      <Button variant="contained" onClick={handleIssue}>Issue Credential</Button>
    </div>
  );
};

export default IssueCredential;
