import React, { useState, useEffect } from 'react';
import { connectWallet } from './utils/web3';
import CreateDID from './components/CreateDID';
import IssueCredential from './components/IssueCredential';
import GrantAccess from './components/GrantAccess';
import RevokeAccess from './components/RevokeAccess';
import GetCredentials from './components/GetCredentials';
import VerifyCredential from './components/VerifyCredential';
import { Button, Box, Typography } from '@mui/material';
import './App.css';
import { LockOpen, Fingerprint } from '@mui/icons-material';
import { ethers } from 'ethers';

function App() {
  const [signer, setSigner] = useState(null);
  const [address, setAddress] = useState('');
  const [did, setDid] = useState('');
  const [credentials, setCredentials] = useState([]);
  const [refresh, setRefresh] = useState(false);

  const handleConnect = async () => {
    const data = await connectWallet();
    if (data) {
      setSigner(data.signer);
      setAddress(data.address);
    }
  };

  // Fetch DID and credentials when address or refresh changes
  useEffect(() => {
    const fetchData = async () => {
      if (signer && address) {
        try {
          const contract = getContract(signer);

          // Fetch DID
          const userDID = await contract.DIDs(address);
          if (userDID) setDid(userDID);

          // Fetch credentials if DID exists
          if (userDID) {
            try {
              await contract.grantAccess(address); // Grant self access
              const creds = await contract.getCredentials(address);
              setCredentials(creds);
            } catch (error) {
              console.error("Error fetching credentials:", error);
            }
          }
        } catch (error) {
          console.error("Error fetching data:", error);
        }
      }
    };
    fetchData();
  }, [signer, address, refresh]);

  useEffect(() => {
    if (window.ethereum) {
      const handleAccountsChanged = async (accounts) => {
        if (accounts.length > 0) {
          const provider = new ethers.BrowserProvider(window.ethereum);
          const signer = await provider.getSigner();
          setSigner(signer);
          setAddress(accounts[0]);
        } else {
          setSigner(null);
          setAddress('');
          setDid('');
          setCredentials([]);
        }
      };

      const handleChainChanged = () => {
        window.location.reload();
      };

      window.ethereum.on('accountsChanged', handleAccountsChanged);
      window.ethereum.on('chainChanged', handleChainChanged);

      const checkConnectedWallet = async () => {
        const accounts = await window.ethereum.request({ method: 'eth_accounts' });
        if (accounts.length > 0) {
          handleAccountsChanged(accounts);
        }
      };
      checkConnectedWallet();

      return () => {
        window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
        window.ethereum.removeListener('chainChanged', handleChainChanged);
      };
    }
  }, []);

  const formatAddress = (addr) => {
    if (!addr) return '';
    return `${addr.substring(0, 6)}...${addr.substring(addr.length - 4)}`;
  };

  const triggerRefresh = () => {
    setRefresh(prev => !prev);
  };

  return (
    <div className="identity-container">
      <h1 className="identity-header">
        <Fingerprint sx={{ fontSize: 36, color: '#8B5CF6' }} />
        Decentralized <span>Identity</span> System
      </h1>

      {!signer ? (
        <div style={{ textAlign: 'center', padding: '40px 0' }}>
          <p style={{ marginBottom: '20px', color: '#666', fontSize: '1.1rem' }}>
            Connect your wallet to manage your decentralized identity
          </p>
          <Button 
            variant="contained" 
            onClick={handleConnect} 
            className="wallet-button"
            startIcon={<LockOpen />}
          >
            Connect Wallet
          </Button>
        </div>
      ) : (
        <div>
          <div style={{ textAlign: 'center', marginBottom: '30px' }}>
            <div className="wallet-address">
              {formatAddress(address)}
            </div>
            {did && (
              <Box sx={{ mt: 2, p: 2, backgroundColor: '#f5f5f5', borderRadius: '8px' }}>
                <Typography variant="subtitle1">Your DID:</Typography>
                <Typography variant="body2" sx={{ wordBreak: 'break-all' }}>{did}</Typography>
              </Box>
            )}
          </div>

          <div className="components-grid">
            <div className="identity-component">
              <CreateDID signer={signer} onSuccess={triggerRefresh} />
              {credentials.length > 0 && (
                <Box sx={{ mt: 3 }}>
                  <Typography variant="h6" gutterBottom>Your Credentials</Typography>
                  <Box sx={{ maxHeight: '300px', overflowY: 'auto' }}>
                    {credentials.map((cred, index) => (
                      <Box key={index} sx={{ p: 2, mb: 2, border: '1px solid #eee', borderRadius: '4px' }}>
                        <Typography variant="subtitle2">Credential ID: {cred.id.toString()}</Typography>
                        <Typography variant="body2">Issuer: {cred.issuer}</Typography>
                        <Typography variant="body2">Data: {cred.dataHash}</Typography>
                        <Typography variant="body2">
                          Issued: {new Date(cred.issuanceTimestamp * 1000).toLocaleString()}
                        </Typography>
                        <Typography variant="body2">
                          Expires: {new Date(cred.expirationTimestamp * 1000).toLocaleString()}
                        </Typography>
                        {cred.isRevoked && (
                          <Typography variant="body2" color="error">Status: Revoked</Typography>
                        )}
                      </Box>
                    ))}
                  </Box>
                </Box>
              )}
            </div>
            <div className="identity-component">
              <IssueCredential signer={signer} onIssue={triggerRefresh} />
            </div>
            <div className="identity-component">
              <GrantAccess signer={signer} />
            </div>
            <div className="identity-component">
              <RevokeAccess signer={signer} />
            </div>
            <div className="identity-component">
              <GetCredentials signer={signer} />
            </div>
            <div className="identity-component">
              <VerifyCredential signer={signer} />
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

