import React, { useState } from 'react';
import { Button, TextField } from '@mui/material';
import { getContract } from '../DecentralizedIdentity';

const GrantAccess = () => {
  const [viewer, setViewer] = useState('');

  const handleGrant = async () => {
    const contract = await getContract();
    if (contract) {
      const tx = await contract.grantAccess(viewer);
      await tx.wait();
      alert('Access granted successfully!');
    }
  };

  return (
    <div>
      <TextField label="Viewer Address" value={viewer} onChange={(e) => setViewer(e.target.value)} fullWidth margin="normal" />
      <Button variant="contained" onClick={handleGrant}>Grant Access</Button>
    </div>
  );
};

export default GrantAccess;
